import React, { useState } from 'react';
//import Data from './apiData';
const Howto= () =>{
    const [boxData] = useState(data);
    return(
        <>
        <div class="row">
        {boxData.map((curElem) => {
            const{image,title,info} = curElem;
            return(
                <>
                    <div class="howtobox col-12 col-lg-4">
                    <img src={image} alt="howtoimg" />
                    <h4 class="mt-20">{title}</h4>
                    <p class="mt-10">{info}</p>
                    </div>               
                </>
            );
        })}
        </div>
        </>
    );
}
export default Howto;

const data = [
    {
        id:1,
        image: ".\src\images\Live_order.png",
        title: "No Minimum Order",
        info: "Order in for yourself or for the group, with no restrictions on order value",
    },
    {
        id:2,
        image: "https://web.archive.org/web/20210903174711im_/https://res.cloudinary.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_224,h_412/4x_Live_order_zzotwy",
        title: "Live Order Tracking",
        info: "Know where your order is at all times, from the restaurant to your doorstep",
    },
    {
        id:3,
        image: "https://web.archive.org/web/20210903174711im_/https://res.cloudinary.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_248,h_376/4x_-_Super_fast_delivery_awv7sn",
        title: "Lightning-Fast Delivery",
        info: "Experience Swiggy's superfast delivery for food delivered fresh & on time",
    },
]
