import React, { useState } from 'react';
//import Data from './apiData';
const Howto= () =>{
    const [boxData , setBoxData] = useState(data);
    return(
        <>
        {boxData.map((curElem) => {
            return(
                <>
                    <section class="row">
                    <div class="howtobox col-lg-4">
                    <img src="https://web.archive.org/web/20210903174711im_/https://res.cloudinary.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_210,h_398/4x_-_No_min_order_x0bxuf" alt="" />
                    <h4 class="mt-10">No Minimum Order</h4>
                    <p class="mt-20">Order in for yourself or for the group, with no restrictions on order value</p>
                    </div>
                    </section>                
                </>
            );
        })}
        </>
    );
}
export default Howto;

const data = [
    {
        id:1,
        image: "https://web.archive.org/web/20210903174711im_/https://res.cloudinary.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_210,h_398/4x_-_No_min_order_x0bxuf",
